<script type="text/javascript">
	var pContent = document.getElementById('algo').innerText;
	var up   = 0;
	var down = 0;
	var basement = 1;
	for (var i = 0; i < pContent.length; i++)
	{
		if(  pContent[i] == '(' )
		{
			basement++;
			up++;
		}
		else
		{
			down++;
			basement--;
		}
		if(basement == -1)
			alert(i);
	}
</script>